import { Component, OnInit } from '@angular/core';
import { InputValue } from 'src/InputValue';
import { ValueService } from './services/value.service';
import { Subscription, interval } from 'rxjs';
import { Router } from '@angular/router';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css'],
})
export class AppComponent {
  title = 'projectView';
  inputValue: InputValue[] = [];
  private refreshPage: Subscription;
  data: any;
  errorMsg: string;
  formData: any;

  mySubscription;

  constructor(private router: Router,private valueService: ValueService) {
    this.router.routeReuseStrategy.shouldReuseRoute = () => false;
      this.mySubscription = this.router.events.subscribe((event) => {
        if (event instanceof NavigationEnd) {
    // Trick the Router into believing it's last link wasn't previously loaded
        this.router.navigated = false;
        }
      }); 
  }

  // reload page in 100 ms
  // ngOnInit() {
  //   this.refreshPage = interval(100).subscribe((val) => {
  //     this.addValues();
  //   });
  // }
  ngOnInit() {
    this.valueService.getAllData().subscribe((data) => {
           this.inputValue = data;
         });
  }

  // private addValues() {
  //   this.valueService.getAllData().subscribe((data) => {
  //     this.inputValue = data;
  //   });
  // }

  onSubmit(formData: any) {
    this.valueService.postData(formData).subscribe(
      (response) => {
        this.data = response;
        this.errorMsg = '';
      },
      (error) => {
        this.errorMsg = 'Error: ' + error.message;
      }
    );
  }

  gOnDestroy(){
    if (this.mySubscription) {
     this.mySubscription.unsubscribe();
    }
  }
}
<div class="container text-center" style="padding-top: 40px">
  <h3>Transcendent Endeavors code challenge</h3>
  <div style="padding: 40px"></div>
  <div class="row row-cols-2">
    <div class="col"><app-add-inputs></app-add-inputs></div>
    <div class="col">
      <h2>Data fetched from the Database</h2>
      <table class="table table-striped table-hover">
        <thead>
          <tr>
            <th scope="col">#</th>
            <th scope="col">Input</th>
            <th scope="col">Multiplied by 2</th>
            <th scope="col">Squared Value</th>
          </tr>
        </thead>
        <tbody>
          <tr *ngFor="let input of inputValue">
            <td>{{ input.id }}</td>
            <td>{{ input.input }}</td>
            <td>{{ input.multiplyBy2 }}</td>
            <td>{{ input.square }}</td>
          </tr>
        </tbody>
      </table>
    </div>
  </div>
</div>

<router-outlet></router-outlet>
