import { HttpClient } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { Observable } from 'rxjs';
import { InputValue } from 'src/InputValue';

@Injectable({
  providedIn: 'root'
})
export class InputServiceService {

  private apiUrl = 'http://localhost:8080/api/multiply'

  constructor(private http: HttpClient) { }
  getAllData(): Observable<any> {
    return this.http.get(`${this.apiUrl}/allData`)
 }

 public addInputBy2(input: object): Observable<object> {
  return this.http.post(`${this.apiUrl}/by2`, input);
}

public addInputSquar(input: object): Observable<object> {
  return this.http.post(`${this.baseUrl}/square`, input);
}

  multiplyBy2(value: InputValue): Observable<InputValue> {
    return this.http.post<InputValue>(this.apiUrl+'/by2', value);
  }

  multipliedSquare(value: InputValue): Observable<InputValue> {
    return this.http.post<InputValue>(this.apiUrl+'/square', value);
  }
}
