import { Component, OnInit } from '@angular/core';
import { InputValue } from 'src/InputValue';
import { ValueService } from './services/value.service';
import { NgbModal } from '@ng-bootstrap/ng-bootstrap';
import { Subscription, interval } from 'rxjs';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css'],
})
export class AppComponent {
  title = 'projectView';
  inputValue: InputValue[] = [];
  private refreshPage: Subscription;
  data: any;
  errorMsg: string;
  formData: any;

  constructor(private valueService: ValueService) {}

  // reload page in 100 ms
  ngOnInit() {
   this.refreshPage = interval(100).subscribe(
    (val) => { this.addValues()}
   );
  }

  private addValues(){
    this.valueService.getAllData().subscribe((data) => {
      this.inputValue = data;
    });
  }

  onSubmit(formData: any) {
    this.valueService.postData(formData).subscribe(
      (response) => {
        this.data = response;
        this.errorMsg = '';
      },
      (error) => {
        this.errorMsg = 'Error: ' + error.message;
      }
    );
  }
}
<div class="container text-center">
  <div class="row">
    <div class="col-sm-5 col-md-6"><app-add-inputs></app-add-inputs></div>
    <div class="col-sm-5 offset-sm-2 col-md-6 offset-md-0">

    </div>
  </div>
</div>






<div class="container" style="padding-top: 40px">
  <div class="row">
    <div class="col-md-6 offset-md-3 text-center">
      <h2>Data fetched from the Database</h2>
      <table class="table table-striped table-hover">
        <thead>
          <tr>
            <th scope="col">#</th>
            <th scope="col">Input</th>
            <th scope="col">Multiplied by 2</th>
            <th scope="col">Squared Value</th>
          </tr>
        </thead>
        <tbody>
          <tr *ngFor="let input of inputValue">
            <td>{{ input.id }}</td>
            <td>{{ input.input }}</td>
            <td>{{ input.multiplyBy2 }}</td>
            <td>{{ input.square }}</td>
          </tr>
        </tbody>
      </table>
    </div>
  </div>
</div>

