import { HttpClient } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { Observable } from 'rxjs';
import { InputValue } from 'src/InputValue';

@Injectable({
  providedIn: 'root'
})
export class InputServiceService {

  private apiUrl = 'http://localhost:8080/api/multiply'

  constructor(private http: HttpClient) { }

  multiply(value: InputValue): Observable<InputValue> {
    return this.http.post<InputValue>(this.apiUrl+'/by2', value);
  }
}
