import { Component } from '@angular/core';
import { InputValue } from 'src/InputValue';
import { ValueService } from '../services/value.service';

@Component({
  selector: 'app-add-inputs',
  templateUrl: './add-inputs.component.html',
  styleUrls: ['./add-inputs.component.css']
})
export class AddInputsComponent {
  inputs: InputValue = new InputValue();
  submitted = false;

  constructor(private valueService: ValueService){}


  addInput(): void{
    this.valueService.addInputs(this.inputs).subscribe(data => {
      console.log(data);
      this.inputs = new InputValue();
    });
  }

  newTutorial(): void {
    this.submitted = false;
    this.inputs = {
      id: 0,
      input: ' ',
      multiplyBy2: ' ',
      square: ' '
    };
  }
}
