  //   });
  // }

  // onClick() {
  //   this.multipliedSquare();
  //   }


  // getMultipliedBy2(value:any) {
  //   return value * 2;
  // }

  // getSquaredValue(value:any) {
  //   return Math.pow(value * 2, 2);
  // }

  onSubmit(formData: any) {
    this.valueService.postData(formData)
      .subscribe(response => {
        this.data = response;
        this.errorMsg = '';
      }, error => {
        this.errorMsg = 'Error: ' + error.message;
      });
  }

}
