import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { InputValue } from 'src/InputValue';
import { InputServiceService } from '../input-service.service';

@Component({
  selector: 'app-input-list',
  templateUrl: './input-list.component.html',
  styleUrls: ['./input-list.component.css']
})
export class InputListComponent implements OnInit{
  inputs: InputValue = new InputValue();

  submitted = false;

  constructor(private inputService: InputServiceService, private router: Router) { }

  ngOnInit() {

  }

  newUser(): void {
    this.submitted = false;
    this.inputs = new InputValue();
  }

  save() {
    this.inputService.multiplyBy2(this.inputs)
      .subscribe(data => console.log(data), error => console.log(error));
    this.inputs = new InputValue();
    this.inputsList();
  }

  onSubmit() {
    this.submitted = true;
    this.save();
  }

  inputsList() {
    this.router.navigate(['inputs']);
  }
}
