import { Component } from '@angular/core';
import { InputValue } from 'src/InputValue';
import { InputServiceService } from '../input-service.service';
import { ValueServiceService } from '../services/value-service.service';

@Component({
  selector: 'app-inputs',
  templateUrl: './inputs.component.html',
  styleUrls: ['./inputs.component.css']
})
export class InputsComponent {
  inputs: InputValue = new InputValue();

  constructor(private inputService: InputServiceService, private valueService: ValueServiceService){}

  ngOnInit() {
    this.valueService.getAllData()
      .subscribe(data => {
        console.log(data)
      })
  }


  multiplyByTwo(): void{
    this.inputService.multiplyBy2(this.inputs).subscribe(data => {
      console.log(data);
      this.inputs = new InputValue();
    });
  }

  multipliedSquare(): void{
    this.inputService.multipliedSquare(this.inputs).subscribe(data => {
      console.log(data);
      this.inputs = new InputValue();
    });
  }
  onSubmit(): void {
    this.multiplyByTwo();
  }

  onClick() {
    this.multipliedSquare();
    }


  getMultipliedBy2(value:any) {
    return value * 2;
  }

  getSquaredValue(value:any) {
    return Math.pow(value * 2, 2);
  }
}
