import { Component, OnInit } from '@angular/core';
import { InputValue } from 'src/InputValue';
import { InputServiceService } from './input-service.service';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent implements OnInit {
  title = 'projectView';
  inputs: InputValue = new InputValue();

  constructor(private inputService: InputServiceService){}

  ngOnInit(): void {

  }

  addPosts(): void{
    this.inputService.createPost(this.inputs).subscribe(data => {
      console.log(data);
      this.inputs = new InputValue();
    });
  }

  onSubmit(): void {
    this.addPosts();
  }
}
