import { Component } from '@angular/core';
import { InputValue } from 'src/InputValue';
import { ValueService } from '../services/value.service';

@Component({
  selector: 'app-list-inputs',
  templateUrl: './list-inputs.component.html',
  styleUrls: ['./list-inputs.component.css']
})
export class ListInputsComponent {

  tutorials?: InputValue[];
  currentTutorial: InputValue = new InputValue();
  currentIndex = -1;
  title = '';

  constructor(private tutorialService: ValueService) {}

  ngOnInit(): void {
    this.retrieveTutorials();
  }

  retrieveTutorials(): void {
    this.tutorialService.getAllData().subscribe({
      next: (data) => {
        this.tutorials = data;
        console.log(data);
      },
      error: (e) => console.error(e)
    });
  }



  setActiveTutorial(tutorial: InputValue, index: number): void {
    this.currentTutorial = tutorial;
    this.currentIndex = index;
  }


}
