import { Component, OnInit } from '@angular/core';
import { InputValue } from 'src/InputValue';
import { ValueService } from './services/value.service';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  title = 'projectView';
  inputValue: InputValue[] = [];
  inputs: InputValue = new InputValue();

  constructor(private valueService: ValueService){}

  ngOnInit() {
    this.valueService.getAllData()
      .subscribe(data => {
        console.log(data)
      })
  }


  multipliedSquare(): void{
    this.valueService.multipliedSquare(this.inputs).subscribe(data => {
      console.log(data);
      this.inputs = new InputValue();
    });
  }

  onClick() {
    this.multipliedSquare();
    }


  getMultipliedBy2(value:any) {
    return value * 2;
  }

  getSquaredValue(value:any) {
    return Math.pow(value * 2, 2);
  }
}
