import { Component, OnInit } from '@angular/core';
import { InputValue } from 'src/InputValue';
import { ValueService } from './services/value.service';
import { NgbModal } from '@ng-bootstrap/ng-bootstrap';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  title = 'projectView';
  inputValue: InputValue[] = [];
  inputs: InputValue = new InputValue();

  constructor(private modalService: NgbModal, private valueService: ValueService){}

  ngOnInit() {
    this.valueService.getAllData()
      .subscribe(data => {
        console.log(data)
      })
  }


  multipliedSquare(): void{
    this.valueService.addInputs(this.inputs).subscribe(data => {
      console.log(data);
      this.inputs = new InputValue();
    });
  }

  onClick() {
    this.multipliedSquare();
    }


  getMultipliedBy2(value:any) {
    return value * 2;
  }

  getSquaredValue(value:any) {
    return Math.pow(value * 2, 2);
  }

  inputData: any = '';



  open(content: any) {
    this.modalService.open(content, { ariaLabelledBy: 'modal-basic-title' });
  }

  onSubmit() {
    this.valueService.addInputs({ input: this.inputData }).subscribe(response => {
      // Handle the response here
      console.log(response);
    });
  }
}
